import React, { useEffect,useState } from 'react'
import styled from 'styled-components'
import { useTable, useGroupBy, useExpanded, useSortBy } from 'react-table'

import mockData  from './mockData'

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }
    }
  }
`

// const columnHelper = createColumnHelper();

const defaultColumns = {
  cell:({ getValue, row: { index }, column: { id }, table }) => {
    console.log(cell);
    const initialValue = getValue()
    // We need to keep and update the state of the cell normally
    const [value, setValue] = React.useState(initialValue)

    // When the input is blurred, we'll call our table meta's updateData function
    const onBlur = () => {
      table.options.meta?.updateData(index, id, value)
    }

    // If the initialValue is changed external, sync it up with our state
    React.useEffect(() => {
      setValue(initialValue)
    }, [initialValue])

    return (
      <input
        value={value}
        onChange={e => setValue(e.target.value)}
        onBlur={onBlur}
      />
    )
  },
};


function Table({ columns, data }) {
  const sortees = React.useMemo(
    () => [
      {
        id: "price",
        desc: false
      }
    ],
    []
  );
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    // setGroupBy,
    prepareRow,
    state: { groupBy, expanded },
  } = useTable(
    {
      columns,
      data,
      defaultColumns,
      meta: {
        updateData: (rowIndex, columnId, value) => {
          // Skip page index reset until after next rerender
          skipAutoResetPageIndex()
          setData(old =>
            old.map((row, index) => {
              if (index === rowIndex) {
                return {
                  ...old[rowIndex],
                  [columnId]: value,
                }
              }
              return row
            })
          )
        },
      },
      debugTable: true,
      initialState: {
        sortBy: sortees,
      }
    },
    // useGroupBy,
    // useSortBy,
    // useExpanded // useGroupBy would be pretty useless without useExpanded ;)
  )

  // We don't want to render all of the rows for this example, so cap
  // it at 100 for this use case
  const firstPageRows = rows.slice(0, 100);

  const getLeafColumns = function (rootColumns) {
    return rootColumns.reduce((leafColumns, column)=>{
        if (column.columns) {
            return [...leafColumns, ...getLeafColumns(column.columns)];
        } else {
            return [...leafColumns, column];
        }
    }, []);
  }

  useEffect(()=>{
    // setGroupBy(['category']);
  },[])

  return (
    <>
      {/* <pre>
        <code>{JSON.stringify({ groupBy, expanded }, null, 2)}</code>
      </pre>
      Group By:
      <select
        value={'category'}
        onChange={e => {
          setGroupBy([e.target.value]);
        }}
      >
        <option value="">None</option>
        {getLeafColumns(columns).map(column => (
          <option key={column.accessor} value={column.accessor}>{column.Header}</option>
        ))}
      </select> */}

      <table {...getTableProps()}>
        <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th {...column.getHeaderProps()}>
                  {/* {column.canGroupBy ? (
                    // If the column can be grouped, let's add a toggle
                    <span {...column.getGroupByToggleProps()}>
                      {column.isGrouped ? 'ðŸ›‘ ' : 'ðŸ‘Š '}
                    </span>
                  ) : null} */}
                  {column.render('Header')}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {firstPageRows.map((row, i) => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => {
                  return (
                    <td
                      // For educational purposes, let's color the
                      // cell depending on what type it is given
                      // from the useGroupBy hook
                      {...cell.getCellProps()}
                      style={{
                        background: cell.isGrouped
                          ? '#0aff0082'
                          : cell.isAggregated
                          ? '#ffa50078'
                          : cell.isPlaceholder
                          ? '#ff000042'
                          : 'white',
                      }}
                    >
                      {cell.isGrouped ? (
                        // If it's a grouped cell, add an expander and row count
                        <>
                          <span {...row.getToggleRowExpandedProps()}>
                            {row.isExpanded ? 'ðŸ‘‡' : 'ðŸ‘‰'}
                          </span>{' '}
                          {cell.render('Cell')} ({row.subRows.length})
                        </>
                      ) : cell.isAggregated ? (
                        // If the cell is aggregated, use the Aggregated
                        // renderer for cell
                        cell.render('Aggregated')
                      ) : cell.isPlaceholder ? null : ( // For cells with repeated values, render null
                        // Otherwise, just render the regular cell
                        cell.render('Cell')
                      )}
                    </td>
                  )
                })}
              </tr>
            )
          })}
        </tbody>
      </table>
      <br />
      <div>Showing the first {rows.length} results of {rows.length} rows</div>
    </>
  )
}


function App() {

  const [data,setData] = useState(mockData(9));
  console.log(data);

  const updatePrice = (row,value) => {
    const tempData = [...data];
    tempData.filter(e=>e.id==row.id)[0].price = value;
    setMockData(tempData);
  }

  const columns = React.useMemo(
    () => [
      {
        Header: 'Table 1',
        columns: [
          {
            Header: 'id',
            accessor: 'id',
          },
          {
            Header: 'image',
            accessor: 'image',
          },
          {
            Header: 'name',
            accessor: 'name',
            // Use a two-stage aggregator here to first
            // count the total rows being aggregated,
            // then sum any of those counts if they are
            // aggregated further
          },
          {
            Header: 'category',
            accessor: 'category',
            // Use another two-stage aggregator here to
            // first count the UNIQUE values from the rows
            // being aggregated, then sum those counts if
            // they are aggregated further
          },
          {
            Header: 'price',
            accessor: row => <input value={row.price} onChange={(e)=>updatePrice(row,e.target.value)}></input>,
            sorted:true,
            editable:true,
            // accessorFn: row => 'sad'+r,
            // Aggregate the average age of visitors
          },
          {
            Header: 'label',
            accessor: 'label',
            // Aggregate the sum of all visits
          },
          {
            Header: 'description',
            accessor: 'description',
          },
        ],
      },
    ],
    []
  )

  // const data = React.useMemo(() => DATA(9), [])

  return (
    <Styles>
      <Table columns={columns} data={data} />
    </Styles>
  )
}

export default App
